mapper.select:
		SELECT U.*, (SELECT COUNT(*) FROM USERS U, NOTIFICATION N where U.USERID=N.USERID and n.notificationread=0 and U.USERID=#{userId}) NOTIFICATIONCOUNT, 
    (SELECT COUNT(*) FROM USERS U, USERPICK UP WHERE U.USERID = UP.USERID) USERPICKCOUNT, USERGRADENAME FROM USERS U, USERGRADE UG 
            WHERE U.USERGRADENO = UG.USERGRADENO AND USERID = #{userId}

mapper.update:
		UPDATE USERS SET userPassword = #{userPassword}, userProfileImage=#{userProfileImage}, userPhone=#{userPhone},
userAddressBasic=#{userAddressBasic}, userAddressDetail=#{userAddressDetail}, userBirthday=#{userBirthday},
userAdPhone=#{userAdPhone}, userAdEmail=#{userAdEmail} WHERE USERID = #{userId}
